/**
 * Types for Magic UI MCP Service
 */
export interface MagicUIComponent {
    name: string;
    description: string;
    metadata?: {
        title: string;
        date: string;
        description: string;
        author: string;
        published: boolean;
        video?: string;
    };
    installation: {
        pnpm: string;
        npm: string;
        yarn: string;
        bun: string;
    };
    props?: {
        name: string;
        type: string;
        default?: string;
        description: string;
    }[];
    usage: string;
    credits?: {
        inspiration?: string;
        inspirationUrl?: string;
    };
    examples?: {
        name: string;
        code: string;
    }[];
}
export interface ButtonComponent extends MagicUIComponent {
    type: "button";
    variant?: string;
}
export interface ComponentDatabase {
    components: MagicUIComponent[];
    getComponentByName(name: string): MagicUIComponent | undefined;
    getComponentsByType(type: string): MagicUIComponent[];
    getAllComponents(): MagicUIComponent[];
}
